import React, { useState } from 'react';
import { Box, Button, TextField, Typography, Card, CardContent, CardMedia, Grid } from '@mui/material';
import { uploadPhoto } from '../services/api';

const UploadForm: React.FC = () => {
    const [photo, setPhoto] = useState<File | null>(null);
    const [prompt, setPrompt] = useState<string>('');
    const [description, setDescription] = useState<string>('');
    const [analysisResult, setAnalysisResult] = useState<any>(null);

    const handlePhotoChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files.length > 0) {
            setPhoto(event.target.files[0]);
        }
    };

    const handleSubmit = async () => {
        if (photo) {
            const formData = new FormData();
            formData.append('photo', photo);
            formData.append('prompt', prompt);
            if (description) {
                formData.append('description', description);
            }
            try {
                const response = await uploadPhoto(formData);
                setAnalysisResult(response.data);
            } catch (error) {
                console.error('Error uploading photo:', error);
            }
        } else {
            console.log('No photo selected');
        }
    };

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 4, mt: 4 }}>
            <Typography variant="h5" gutterBottom>Upload Photo for Analysis</Typography>
            <TextField
                label="Prompt"
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                fullWidth
                variant="outlined"
            />
            <TextField
                label="Description (optional)"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                fullWidth
                variant="outlined"
            />
            <Button variant="contained" component="label">
                Select Photo
                <input type="file" hidden accept="image/*" onChange={handlePhotoChange} />
            </Button>
            <Button variant="contained" onClick={handleSubmit} disabled={!photo}>
                Submit
            </Button>

            {analysisResult && (
                <Card sx={{ maxWidth: 800, mt: 4 }}>
                    <CardMedia
                        component="img"
                        alt="Uploaded"
                        height="400"
                        image={`http://localhost:3000${analysisResult.filePath}`}
                        title="Uploaded Photo"
                    />
                    <CardContent>
                        <Typography variant="h6" gutterBottom>
                            Analysis Result
                        </Typography>
                        <Grid container spacing={2}>
                            <Grid item xs={6}>
                                <Typography variant="body1" color="textSecondary">
                                    <strong>Score:</strong> {analysisResult.analysis.score}
                                </Typography>
                            </Grid>
                            <Grid item xs={6}>
                                <Typography variant="body1" color="textSecondary">
                                    <strong>Recommendations:</strong> {analysisResult.analysis.recommendations}
                                </Typography>
                            </Grid>
                        </Grid>
                    </CardContent>
                </Card>
            )}
        </Box>
    );
};

export default UploadForm;
