// src/theme.ts

import { createTheme } from '@mui/material/styles';

// Create a theme instance
const theme = createTheme({
    palette: {
        primary: {
            main: '#1976d2', // Customize your primary color here
        },
        secondary: {
            main: '#dc004e', // Customize your secondary color here
        },
        background: {
            default: '#f5f5f5', // Background color for the app
        },
    },
    typography: {
        fontFamily: 'Roboto, Arial, sans-serif', // Set the default font family
        h1: {
            fontSize: '2.5rem',
            fontWeight: 500,
        },
        h2: {
            fontSize: '2rem',
            fontWeight: 500,
        },
        h3: {
            fontSize: '1.75rem',
            fontWeight: 500,
        },
        h4: {
            fontSize: '1.5rem',
            fontWeight: 500,
        },
        h5: {
            fontSize: '1.25rem',
            fontWeight: 500,
        },
        h6: {
            fontSize: '1rem',
            fontWeight: 500,
        },
        body1: {
            fontSize: '1rem',
            fontWeight: 400,
        },
        body2: {
            fontSize: '0.875rem',
            fontWeight: 400,
        },
    },
    components: {
        MuiButton: {
            styleOverrides: {
                root: {
                    borderRadius: 8, // Example of customizing a component's style
                    textTransform: 'none', // Remove uppercase text transformation
                },
            },
        },
        MuiTextField: {
            styleOverrides: {
                root: {
                    marginBottom: '16px', // Add consistent spacing below text fields
                },
            },
        },
    },
});

export default theme;
